ignore_pat = re.compile('[' + chars_to_ignore + ']')


def ranges(i):
    for a, b in itertools.groupby(enumerate(i), lambda r: r[1] - r[0]):
        b = list(b)
        yield b[0][1], b[-1][1]

if __name__ == '__main__':
    unicode_category = defaultdict(list)
    for c in map(chr, range(sys.maxunicode + 1)):
        unicode_category[unicodedata.category(c)].append(c)
    igchars = []
    for c in IGNORED_CATEGORIES:
        igchars += unicode_category[c]
    igchars = sorted(map(ord, igchars))
    ans = []
    for l, r in ranges(igchars):
        if l == r:
            ans.append(chr(l))
        else:
            ans.append('{}-{}'.format(chr(l), chr(r)))
    igchars = ''.join(ans)

    with open(__file__, encoding='utf-8') as f:
        raw = f.read()
    nraw = re.sub(r'^chars_to_ignore = .+$', ('chars_to_ignore = {!r} # noqa {{' '{{' '{{').format(igchars), raw, flags=re.MULTILINE)
    print(nraw)
    with open(__file__, 'w', encoding='utf-8') as f:
        f.write(nraw)
